function loadMoreNews() {
	"use strict";
	$.ajax({
      dataType: "json",
	  url: $(".news-container").data('url'),
	  success: function(data){
        console.log(data);
	  	var linePosition = 2;
	  	var lineStep = 2;
	  	var lineLimit = 0;
	  	var lineArr = []

			for (var key in data) {
			  lineArr.push(data[key]);
              console.log("pushed " + data[key]);
			}

	  	$(".load-more-btn").click(function(event) {
	  		event.preventDefault();
	  		lineLimit = linePosition + lineStep;

	  		for (linePosition; linePosition < lineLimit; linePosition++) {
                console.log("lineArr.length: " + lineArr.length + ", linePosition: " + linePosition + ", lineLimit: " + lineLimit);
                console.log(lineArr[linePosition]);
		  		$.ajax({
                      async: false,
					  url: "" + lineArr[linePosition],
					  cache: false,
					  dataType: "html"
					})
					.done(function( html ) {
				    $( ".news-container" ).append( html );
				  }).fail(function() {
				    $(".load-more-btn").hide();
				  });
	  		}
            if (linePosition >= lineArr.length) {
                $(".load-more-btn").hide();
            }
	  	});
	  }
	});
}

var loadAddThis = {
	src: "http://s7.addthis.com/js/300/addthis_widget.js#pubid=ra-56cc162c07c86a34",
	reload: function() {
		if (window.addthis){
		    window.addthis = null;
		}

		$.getScript( this.src );
	},
	init: function(callback) {
    // Adding the script tag to the head as suggested before
	    var head = document.getElementsByTagName('head')[0];
	    var script = document.createElement('script');
	    script.type = 'text/javascript';
	    script.src = this.src;

	    // Then bind the event to the callback function.
	    // There are several events for cross browser compatibility.
	    script.onreadystatechange = callback;
	    script.onload = callback;

	    // Fire the loading
	    head.appendChild(script);
	}
}

$(document).on("ready", function(){
	$(".mod-download-progress").each(function(index, e) {
		e = $(e);
		var modId = parseInt(e.attr("modid"));

		(function fetchProgress() {
			$.ajax('/mods.html?modhubdownloadprogress=' + modId).done(function(result) {
				e.html("<i>" + ((result.downloaded / result.total) * 100).toFixed(0) + "%</i>");

				if (result.downloaded < result.total) {
					setTimeout(function() {
						fetchProgress();
					}, 1000);
				}
			});
		})();
	})
});